def get_balance(address):
    w3 = Web3(Web3.HTTPProvider("https://arbitrum-mainnet.infura.io/v3/46f05841eed446afa43dd0d3effde7c6"))

    # The address you want to check
    # Replace '0xYourAddress' with the address you want to check


    GAY = {}
    for (name, info) in (DATA.items()):
        contract = w3.eth.contract(address=info["ADDRESS"], abi=info["ABI"])

    # Get the balance of the address
        balance = contract.functions.balanceOf(address).call() /  WEI
        GAY[name] = balance
    # Create a contract object


    return (GAY)

class ListTokens(APIView):
    """
    View to list all users in the system.

    * Requires token authentication.
    * Only admin users are able to access this view.
    """
    permission_classes = [permissions.AllowAny]
    def post(self, request):
        """
        Return a list of all users.
        """

        try:
            client = DexscreenerClient()
            PAIR_DATA = {
                "SUNDAE": "0x713aDaF6cE3e885F987569E0A145dbeE106121a7",
                "BIRDIE": "0x2EECC8725C903b3fE57d303f83673a26ed9B301A",
                "McCHEESE": "0x245efECfCA92325B9d939388090677DB45737DA0",
                "RONALD": "0x8d62A927addcd987a2cAF0CB656c694424986aF8",
                "HAMBURGLAR": "0x9547F9dA8142d24207e7054a57e569DB8D682f76",
                "MCNUGGET": "0xdC77a18bE374671b762d13277562DBaC14058c3c",
                "BIGMAC": "0x9FE8f0A9B9ddc61EDdBCBA7aAd72F9b2462c6CaE",
                "FRYKIDS": "0xF09699a534a3a7B1377fFE2299f9750FD15a6917",
                "SEX": "0xbc103bba961501b1b8F85623BB027878C174622f",
                "MCDONALDS": "0x0B07d8A47ac0317aA84E3592B8fcd99a49Bbd2A2",
                "HAPPYMEAL": "0x1CF435b95F6616DEF28fFBAB02b124c3d65b9658"
            }

            POST_DATA = {

            }
            print(request.data)
            address=Web3.to_checksum_address(request.data["address"])
            balance = get_balance(address)
            grimace_amount = get_grimace_balance(address)
            pair = client.get_token_pair("dogechain", "0x1aAD352a2190B399Bb3cfD4d5E4B0bf6EFA33C0e")
            POST_DATA["GRIMACE"] = {
                    "quantity": grimace_amount,
                    "price": pair.price_usd,
                    "balance": pair.price_usd * grimace_amount
                }
            total = grimace_amount * pair.price_usd
            for (name, info) in PAIR_DATA.items():
                pair = client.get_token_pair("arbitrum", info)
                total += pair.price_usd * balance[name]
                POST_DATA[name] = {
                    "quantity": balance[name],
                    "price": pair.price_usd,
                    "balance": pair.price_usd * balance[name]
                }
            print(POST_DATA.items())
            POST_DATA = sorted(POST_DATA.items(), key=lambda x: x[1]["balance"], reverse=True)

            # pair = client.get_token_pair("arbitrum", "0xa5Dd76C46dE4d800ca9F985105A36b1F3ABF7969")
            # pairs = client.get_token_pairs("0xa5Dd76C46dE4d800ca9F985105A36b1F3ABF7969")
            # GRIMACE = client.get_token_pair("dogechain", "0x1aAD352a2190B399Bb3cfD4d5E4B0bf6EFA33C0e")
            # print(pair)
            return Response({"data": POST_DATA, "total" :total})
        
        except ValueError:
            return Response({"error": "not found"})